package TypeCasting;

public class TypeCasting {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		// implicit
		int num = 10;
		double num2 = num;
		System.out.println(num2);
		// explicit
		double num3 = 9.6;
		int num4 = (int) num3;
		System.out.println(num4);
	}

}